	public static List<String> tokenize(String text) 
	{
		char inputChar[] = text.toCharArray();
		
		ArrayList<String> wordList = new ArrayList<String>();
		
		int startIndex=0;
		int i=0;
		int endIndex =inputChar.length-1;
		for(;i<endIndex;i++)
		{
			char ch = inputChar[i]; 
			if(ch=='-')
			{
				if(startIndex==i)startIndex++;
			}
			else if( (ch<0x0980||ch>0x09E3) && (ch<0x200A||ch>0x200E) )
			{
				if(startIndex==i)startIndex++;
				else
				{
					int discardEndCharLen=0;
					while(inputChar[i-discardEndCharLen]=='-')discardEndCharLen++;
					String word = new String (inputChar,startIndex, i-startIndex-discardEndCharLen);
					wordList.add(word);
					startIndex = i+1;
				}
			}
		}

		if(startIndex<i)
		{
			int lastCharLen=1;
			char ch = inputChar[endIndex]; 
			if(ch=='-') 
			{
				lastCharLen=1;
			}
			else if((ch<0x0980||ch>0x09E3)	&&(ch<0x200A||ch>0x200E)	)
				lastCharLen=0;						

			String word = new String (inputChar,startIndex, i-startIndex+lastCharLen);
			wordList.add(word);
		}				
		
		
		return wordList;
	}
	
